    }
    const handleValueChange = (event) => {
        // event.preventDefault();
        // const fromValue = {
        //     title:val
        // };
        // setVal('');
        setVal(event.target.value);
    }
    return (
        <div className="todolist">
            <div className="name">TodoList</div>
            <ul className="list-todo">
                {todo.map((item, index) => (
                    <li key={index}>
                        <span>{item}</span>  
                        <button onClick={onRemoveToList}>x</button>
