import React, { useState } from 'react'
import './TodoList';

const TodoList = () => {
    // data cho list
    const [todo, setTodo] = useState([]);
    // state cho input
    const [val, setVal] = useState('');
    const handleValueChange = (event) => {
        if (event.keyCode === 13) {
            setVal(event.target.value);
        }

    }
    // on add item to TodoList
    const onAddItemToList = () => {
        // const _todo = [...todo];
        // _todo.push(val);
        // setTodo(_todo);

        setTodo([...todo, val])
    }
    const onRemoveToList = () => {


    }

    return (
        <div className="todolist">
            <div className="name">TodoList</div>
            <ul className="list-todo">
                {todo.map((item, index) => (
                    <li key={index}>
                        <span>{item}</span>
                        <button onClick={onRemoveToList}>x</button>
                    </li>
                ))}
            </ul>
            <div className="input-group" >
                <input type="text" placeholder="nhap" onChange={handleValueChange} />
                <button onClick={onAddItemToList}>add</button>
            </div>
        </div>
    )
}
export default TodoList;
