import React, { useState } from 'react'
import './TodoList';

const TodoList = () => {
    // data cho list
    const [todo, setTodo] = useState([]);
    // state cho input
    const [val, setVal] = useState('');
    const handleValueChange = (event) => {

        setVal(event.target.value);
    }
    // on add item to TodoList
    const onAddItemToList = (event) => {
        // const _todo = [...todo];
        // _todo.push(val);
        // setTodo(_todo);
        const checkVal = todo.find(v => v === val);
        if (event.keyCode === 13) {
            if(!checkVal) {
                setTodo([...todo, val])
            }
            else{
                alert('trung roi ko nhap dc')
                // setVal('');
            }
            setVal('');
            
        }
        
    }
    const clickItemToList = () => {
        const checkVal = todo.find(v => v === val);
        if(!checkVal) {
            setTodo([...todo, val])
        }
    }

    const onRemoveToList = () => {


    }

    return (
        <div className="todolist">
            <div className="name">TodoList</div>
            <ul className="list-todo">
                {todo.map((item, index) => (
                    <li key={index}>
                        <span>{item}</span>
                        <button onClick={onRemoveToList}>x</button>
                    </li>
                ))}
            </ul>
            <div className="input-group" >
                <input
                    type="text"
                    placeholder="nhap"
                    value={val}
                    onKeyUp={onAddItemToList}
                    onChange={handleValueChange}
                />
                <button onClick={clickItemToList}>add</button>
            </div>
        </div>
    )
}
export default TodoList;
